import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const PropertyDetail = () => {
  const { id } = useParams();
  const [property, setProperty] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/properties/${id}`)
      .then(res => {
        setProperty(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Error fetching property:', err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="p-4">Loading...</div>;
  if (!property) return <div className="p-4">Property not found.</div>;

  return (
    <div className="max-w-4xl mx-auto mt-8 p-4 bg-white shadow rounded">
      <img
        src={property.image}
        alt={property.title}
        className="w-full h-64 object-cover rounded mb-4"
      />
      <h1 className="text-3xl font-bold mb-2">{property.title}</h1>
      <p className="text-gray-600 mb-2">üìç {property.location}</p>
      <p className="text-green-700 text-xl font-semibold mb-4">‚Çπ{property.price}</p>
      <p className="text-gray-700 leading-relaxed">{property.description}</p>
    </div>
  );
};

export default PropertyDetail;
