import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const PropertyList = () => {
  const [properties, setProperties] = useState([]);
  const [search, setSearch] = useState('');
  const navigate = useNavigate();

  const fetchProperties = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/properties');
      setProperties(res.data);
    } catch (error) {
      console.error('Error fetching properties:', error);
    }
  };

  useEffect(() => {
    fetchProperties();
  }, []);

  const filtered = properties.filter((prop) =>
    prop.city.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Available Properties</h1>
      <input
        type="text"
        placeholder="Search by city..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="mb-4 border px-3 py-2 w-full max-w-md"
      />

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
        {filtered.map((prop) => (
          <div
            key={prop._id}
            className="border p-4 rounded shadow cursor-pointer"
            onClick={() => navigate(`/property/${prop._id}`)}
          >
            <img src={prop.image} alt={prop.title} className="h-40 w-full object-cover mb-2 rounded" />
            <h2 className="text-lg font-semibold">{prop.title}</h2>
            <p>{prop.city}</p>
            <p className="text-green-700 font-bold">â‚¹{prop.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PropertyList;
