import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

function PropertyList() {
  const [properties, setProperties] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [search, setSearch] = useState('');
  const [cityFilter, setCityFilter] = useState('');
  const [priceRange, setPriceRange] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 4;

  useEffect(() => {
    fetch('http://localhost:5000/api/properties')
      .then(res => res.json())
      .then(data => {
        setProperties(data);
        setFiltered(data);
      });
  }, []);

  useEffect(() => {
    let result = [...properties];

    // Filter by search
    if (search) {
      result = result.filter(
        p =>
          p.title.toLowerCase().includes(search.toLowerCase()) ||
          p.city.toLowerCase().includes(search.toLowerCase())
      );
    }

    // Filter by city
    if (cityFilter) {
      result = result.filter(p => p.city === cityFilter);
    }

    // Filter by price
    if (priceRange === 'low') {
      result = result.filter(p => p.price <= 1000000);
    } else if (priceRange === 'mid') {
      result = result.filter(p => p.price > 1000000 && p.price <= 5000000);
    } else if (priceRange === 'high') {
      result = result.filter(p => p.price > 5000000);
    }

    setFiltered(result);
    setCurrentPage(1); // Reset to page 1 on filter
  }, [search, cityFilter, priceRange, properties]);

  // Pagination Logic
  const indexOfLast = currentPage * itemsPerPage;
  const indexOfFirst = indexOfLast - itemsPerPage;
  const currentItems = filtered.slice(indexOfFirst, indexOfLast);
  const totalPages = Math.ceil(filtered.length / itemsPerPage);

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">üè° Properties for Sale</h1>

      {/* Search & Filters */}
      <div className="flex flex-wrap gap-2 mb-4">
        <input
          type="text"
          placeholder="Search by title or city"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="p-2 border rounded"
        />
        <select
          value={cityFilter}
          onChange={(e) => setCityFilter(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="">All Cities</option>
          {[...new Set(properties.map(p => p.city))].map(city => (
            <option key={city}>{city}</option>
          ))}
        </select>
        <select
          value={priceRange}
          onChange={(e) => setPriceRange(e.target.value)}
          className="p-2 border rounded"
        >
          <option value="">All Prices</option>
          <option value="low">Under ‚Çπ10L</option>
          <option value="mid">‚Çπ10L ‚Äì ‚Çπ50L</option>
          <option value="high">Above ‚Çπ50L</option>
        </select>
      </div>

      {/* Listings */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {currentItems.map((property) => (
          <div key={property._id} className="border rounded p-4 shadow bg-white">
            <img
              src={property.image}
              alt={property.title}
              className="h-48 w-full object-cover rounded mb-2"
            />
            <h2 className="text-xl font-bold">{property.title}</h2>
            <p className="text-sm text-gray-600">{property.city}</p>
            <p className="text-green-600 font-semibold">‚Çπ {property.price.toLocaleString()}</p>
            <Link to={`/property/${property._id}`}>
              <button className="mt-2 bg-blue-500 text-white px-3 py-1 rounded">
                View Details
              </button>
            </Link>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <div className="mt-4 flex gap-2">
        {Array.from({ length: totalPages }, (_, idx) => idx + 1).map((page) => (
          <button
            key={page}
            onClick={() => setCurrentPage(page)}
            className={`px-3 py-1 rounded border ${
              currentPage === page ? 'bg-blue-500 text-white' : 'bg-white'
            }`}
          >
            {page}
          </button>
        ))}
      </div>
    </div>
  );
}

export default PropertyList;
