// src/pages/PropertyList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function PropertyList() {
  const [properties, setProperties] = useState([]);
  const [search, setSearch] = useState('');
  const [city, setCity] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5000/api/properties')
      .then(res => setProperties(res.data))
      .catch(err => console.log(err));
  }, []);

  const filtered = properties.filter((p) =>
    (p.title.toLowerCase().includes(search.toLowerCase()) ||
     p.description.toLowerCase().includes(search.toLowerCase())) &&
    (city ? p.city === city : true)
  );

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Available Properties</h1>

      {/* Search & Filter */}
      <div className="flex flex-col sm:flex-row gap-2 mb-4">
        <input
          className="border p-2 flex-1"
          type="text"
          placeholder="Search properties..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
        <select className="border p-2" value={city} onChange={e => setCity(e.target.value)}>
          <option value="">All Cities</option>
          <option value="Ludhiana">Ludhiana</option>
          <option value="Jalandhar">Jalandhar</option>
          <option value="Chandigarh">Chandigarh</option>
          <option value="Mohali">Mohali</option>
        </select>
      </div>

      {/* Listings */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {filtered.map(p => (
          <Link to={`/property/${p._id}`} key={p._id} className="border p-4 rounded shadow">
            <img src={p.image} alt={p.title} className="h-48 w-full object-cover rounded mb-2" />
            <h2 className="text-lg font-semibold">{p.title}</h2>
            <p>{p.city} • ₹{p.price}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}

export default PropertyList;
