import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/properties')
      .then((res) => setProperties(res.data))
      .catch((err) => console.log(err));
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/properties/${id}`);
      setProperties(properties.filter((prop) => prop._id !== id));
    } catch (error) {
      console.error('Failed to delete:', error);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">ðŸ“Š Admin Dashboard</h1>
      {properties.map((property) => (
        <div key={property._id} className="border p-4 mb-2 rounded flex justify-between items-center">
          <div>
            <h2 className="font-bold">{property.title}</h2>
            <p>{property.location} - â‚¹{property.price}</p>
          </div>
          <button
            onClick={() => handleDelete(property._id)}
            className="bg-red-500 text-white px-3 py-1 rounded"
          >
            Delete
          </button>
        </div>
      ))}
    </div>
  );
};

export default AdminDashboard;
