import React, { useState } from 'react';
import axios from 'axios';

const UploadPage = () => {
  const [form, setForm] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
  });
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('file', image);
    formData.append('upload_preset', 'YOUR_UPLOAD_PRESET'); // replace
    const res = await axios.post('https://api.cloudinary.com/v1_1/YOUR_CLOUD_NAME/image/upload', formData); // replace
    return res.data.secure_url;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const imageUrl = await handleImageUpload();

      const res = await axios.post(
        'http://localhost:5000/api/properties',
        { ...form, image: imageUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setMessage('✅ Property uploaded successfully!');
      setForm({ title: '', description: '', price: '', location: '' });
      setImage(null);
    } catch (err) {
      console.error(err);
      setMessage('❌ Failed to upload. Make sure you are logged in.');
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-10 bg-white p-6 rounded shadow">
      <h2 className="text-2xl font-bold mb-4">Upload Property</h2>
      {message && <p className="mb-3 text-sm">{message}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        <input type="text" name="title" placeholder="Title" value={form.title} onChange={handleChange} className="w-full p-2 border rounded" />
        <input type="text" name="location" placeholder="Location" value={form.location} onChange={handleChange} className="w-full p-2 border rounded" />
        <input type="number" name="price" placeholder="Price" value={form.price} onChange={handleChange} className="w-full p-2 border rounded" />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} className="w-full p-2 border rounded" />
        <input type="file" onChange={(e) => setImage(e.target.files[0])} className="w-full p-2 border rounded" />
        <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded">Upload</button>
      </form>
    </div>
  );
};

export default UploadPage;
