import React, { useState } from 'react';
import axios from 'axios';

const UploadProperty = () => {
  const [form, setForm] = useState({
    title: '',
    description: '',
    price: '',
    location: '',
    city: '',
    image: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post('http://localhost:5000/api/properties', form, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Property uploaded!');
    } catch (err) {
      alert('Upload failed');
    }
  };

  return (
    <div className="max-w-lg mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4">Upload Property</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input name="title" placeholder="Title" onChange={handleChange} className="w-full p-2 border rounded" />
        <textarea name="description" placeholder="Description" onChange={handleChange} className="w-full p-2 border rounded" />
        <input name="price" type="number" placeholder="Price" onChange={handleChange} className="w-full p-2 border rounded" />
        <input name="location" placeholder="Location" onChange={handleChange} className="w-full p-2 border rounded" />
        <input name="city" placeholder="City" onChange={handleChange} className="w-full p-2 border rounded" />
        <input name="image" placeholder="Image URL" onChange={handleChange} className="w-full p-2 border rounded" />
        <button type="submit" className="bg-green-500 text-white px-4 py-2 rounded">Upload</button>
      </form>
    </div>
  );
};

export default UploadProperty;
